// src/components/Dates.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import '../styles/Dates.css';

const Dates = () => {
    const { userId } = useParams();
    const [bookedDates, setBookedDates] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchBookedDates = async () => {
            try {
                const response = await fetch(`/api/profile/business/${userId}/booked-dates`);
                if (!response.ok) {
                    throw new Error('Failed to fetch booked dates');
                }
                const data = await response.json();
                setBookedDates(data.bookedDates);
            } catch (error) {
                console.error('Error fetching booked dates:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchBookedDates();
    }, [userId]);

    if (loading) {
        return <p>Loading...</p>;
    }

    const bookedDatesSet = new Set(bookedDates.map(date => new Date(date.booking_date)));

    return (
        <div className="dates">

            <h2>Booked Dates</h2>
            <Calendar
                tileDisabled={({ date }) => bookedDatesSet.has(date)}
            />
        </div>
    );
};

export default Dates;
