import React from 'react';

const MealSelection = ({
    exclusiveMeal,
    regularMeals,
    selectedMeal,
    onMealSelection,
    totalPrice,
    advancePayment,
    onBooking,
    onSkipMeal,
}) => {
    return (
        <div className='meals-container'>
            <h3>Select a Meal for Your Event</h3>
            <div className='meals-container'>
                <h4>Exclusive Meals</h4>
                <ul>
                    {exclusiveMeal && (
                        <li
                            key={exclusiveMeal.id}
                            className={`meal-option ${selectedMeal?.id === exclusiveMeal.id ? "selected" : ""}`}
                            onClick={() => onMealSelection(exclusiveMeal)}
                        >
                            <input
                                type='radio'
                                name='meal'
                                value={exclusiveMeal.id}
                                checked={selectedMeal?.id === exclusiveMeal.id}
                                onChange={() => onMealSelection(exclusiveMeal)}
                            />
                            {exclusiveMeal.name} - ${exclusiveMeal.price}
                            <img src={exclusiveMeal.image} alt={exclusiveMeal.name} className='meal-photo' />
                        </li>
                    )}
                </ul>

                <h4>Regular Meals</h4>
                <ul>
                    {regularMeals.map((meal) => (
                        <li
                            key={meal.id}
                            className={`meal-option ${selectedMeal?.id === meal.id ? "selected" : ""}`}
                            onClick={() => onMealSelection(meal)}
                        >
                            <input
                                type='radio'
                                name='meal'
                                value={meal.id}
                                checked={selectedMeal?.id === meal.id}
                                onChange={() => onMealSelection(meal)}
                            />
                            {meal.name} - ${meal.price}
                            <img src={meal.image} alt={meal.name} className='meal-photo' />
                        </li>
                    ))}
                </ul>
            </div>
            <h4>Total Price: ${totalPrice}</h4>
            <h4>Advance Payment (5%): ${advancePayment}</h4>
            <button onClick={onBooking}>Confirm and Book</button>
            <button onClick={onSkipMeal}>Skip Meal</button>
        </div>
    );
};

export default MealSelection;
