// src/components/BusinessUserRegister.js
import React, { useState } from 'react';
import { API_URL } from '../constans';

const BusinessUserRegister = () => {
  const [businessName, setBusinessName] = useState('');
  const [numOfBusiness, setNumOfBusiness] = useState('');
  const [address, setAddress] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const response = await fetch(`${API_URL}/api/register/business`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ businessName, numOfBusiness, address, email, password }),
      });

      const text = await response.text();
      try {
        const data = JSON.parse(text);
        if (response.ok) {
          alert(data.message);
        } else {
          setError(data.message || 'Error registering business user');
        }
      } catch (error) {
        console.error('Error parsing JSON:', error, 'Response:', text);
        setError('Error registering business user. Please check the console for more details.');
      }
    } catch (error) {
      console.error('Error registering business user:', error);
      setError('Error registering business user. Please try again.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label>Business Name:</label>
        <input type="text" value={businessName} onChange={(e) => setBusinessName(e.target.value)} />
      </div>
      <div className="form-group">
        <label>Number of Business:</label>
        <input type="text" value={numOfBusiness} onChange={(e) => setNumOfBusiness(e.target.value)} />
      </div>
      <div className="form-group">
        <label>Address:</label>
        <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} />
      </div>
      <div className="form-group">
        <label>Email:</label>
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
      </div>
      <div className="form-group">
        <label>Password:</label>
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      </div>
      <button type="submit">Register</button>
      {error && <p>{error}</p>}
    </form>
  );
};

export default BusinessUserRegister;
