import React, { useState } from 'react';

const BusinessProfileDetailsDisplay = ({ userDetails, setEditing }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [hallCapacity, setHallCapacity] = useState(userDetails.hallCapacity || '');

  const handleEdit = () => {
    setIsEditing(true);
    setHallCapacity(userDetails.hallCapacity); // Set the current hall capacity when entering edit mode
  };

  const handleUpdate = async () => {
    // Logic to handle the update of hallCapacity
    try {
      const response = await fetch(`http://localhost:3000/api/business/${userDetails.id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ...userDetails,
          hallCapacity: hallCapacity, // Updated hallCapacity
        }),
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        setIsEditing(false); // Exit editing mode
      } else {
        alert(data.message || "Error updating hall capacity");
      }
    } catch (error) {
      console.error("Error updating hall capacity:", error);
      alert("Error updating hall capacity. Please try again.");
    }
  };

  return (
    <div>
      <p><strong>Business Name:</strong> {userDetails.businessName}</p>
      <p><strong>Address:</strong> {userDetails.address}</p>
      <p><strong>Email:</strong> {userDetails.email}</p>
      <p><strong>Category:</strong> {userDetails.category_name || 'N/A'}</p>

      {/* Conditionally render Hall Capacity based on category */}
      {userDetails.category_name === 'Hall' && (
        <>
          {isEditing ? (
            <div>
              <label>Hall Capacity:</label>
              <input
                type="number"
                value={hallCapacity}
                onChange={(e) => setHallCapacity(e.target.value)}
                min="0" // Prevent negative input
              />
              <button onClick={handleUpdate}>Save</button>
              <button onClick={() => setIsEditing(false)}>Cancel</button>
            </div>
          ) : (
            <p><strong>Hall Capacity:</strong> {userDetails.hallCapacity || 'N/A'}</p>
          )}
        </>
      )}

      {/* Always show the Edit button */}
      <button onClick={handleEdit}>Edit</button>
    </div>
  );
};

export default BusinessProfileDetailsDisplay;
