import React from "react";

const MealOption = ({ meal, selectedMeal, handleMealSelection }) => {
  return (
    <li
      className={`meal-option ${
        selectedMeal?.id === meal.id ? "selected" : ""
      }`}
      onClick={() => handleMealSelection(meal)}
    >
      <input
        type="radio"
        name="meal"
        value={meal.id}
        checked={selectedMeal?.id === meal.id}
        onChange={() => handleMealSelection(meal)}
      />
      {meal.name} - ${meal.price}
      <img src={meal.image} alt={meal.name} className="meal-photo" />
    </li>
  );
};

export default MealOption;
