import React, { useEffect } from 'react';

const BusinessProfileDetailsDisplay = ({ userDetails, setEditing }) => {

  useEffect(() => {
    // Set the category in localStorage when userDetails.category_name changes
    localStorage.setItem('category', userDetails.category_name);
  }, [userDetails.category_name]); // Dependency array to run effect when category changes

  return (
    <div>
      <p><strong>Business Name:</strong> {userDetails.businessName}</p>
      <p><strong>Address:</strong> {userDetails.address}</p>
      <p><strong>Email:</strong> {userDetails.email}</p>
      <p><strong>Category:</strong> {userDetails.category_name || 'N/A'}</p>
      <p><strong>Price per Event:</strong> {userDetails.price_per_event || 'N/A'}</p>

      {/* Conditionally render hall capacity if category is "Hall" */}
      {userDetails.category_name === "Hall" && (
        <p><strong>Hall Capacity:</strong> {userDetails.hallCapacity || 'N/A'}</p>
      )}

      <button onClick={() => setEditing(true)}>Edit</button>
    </div>
  );
};

export default BusinessProfileDetailsDisplay;
