import React, { useContext } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import Register from './components/RegularUserRegister';
import BusinessUserRegister from './components/BusinessUserRegister';
import Profile from './pages/Profile';
import BusinessProfile from './pages/BusinessProfile';
import Home from './pages/Home'; // Import the Home component
import AuthContext from './contexts/AuthContext';
import Layout from './components/Layout';

const App = () => {
  const { isAuthenticated } = useContext(AuthContext);
  const userType = localStorage.getItem('userType');

  return (
    <Router>
      <Layout>
        <Routes>
          <Route path="/" element={<Home />} /> {/* Home route */}
          <Route
            path="/login"
            element={isAuthenticated ? <Navigate to="/profile" /> : <Login />}
          />
          <Route
            path="/register"
            element={isAuthenticated ? <Navigate to="/profile" /> : <Register />}
          />
          <Route
            path="/register-business"
            element={isAuthenticated ? <Navigate to="/profile" /> : <BusinessUserRegister />}
          />
          <Route
            path="/profile"
            element={
              isAuthenticated ? (
                userType === 'business' ? <BusinessProfile /> : <Profile />
              ) : (
                <Navigate to="/login" />
              )
            }
          />
          <Route
            path="*"
            element={<Navigate to="/" />} // Redirect any unknown paths to home
          />
        </Routes>
      </Layout>
    </Router>
  );
};

export default App;
