import React, { useState } from 'react';

const InviteForm = ({ onSubmit }) => {
  const [inviteCount, setInviteCount] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!inviteCount) {
      alert('Please enter the number of invited people.');
      return;
    }
    onSubmit(inviteCount); // Pass the invite count to the parent component
    setInviteCount(''); // Reset the input field after submission
  };

  return (
    <form onSubmit={handleSubmit} className="invite-form">
      <label htmlFor="inviteCount">Number of Invited People:</label>
      <input
        type="number"
        id="inviteCount"
        name="inviteCount"
        value={inviteCount}
        onChange={(e) => setInviteCount(e.target.value)}
        required
      />
      <button type="submit">Submit</button>
    </form>
  );
};

export default InviteForm;
