import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { API_URL } from '../constans';
import '../styles/styles.css';

const RegularProfile = () => {
  const [userDetails, setUserDetails] = useState({});
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});
  const navigate = useNavigate();

  const userId = localStorage.getItem('userId');

  // Function to format date
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: '2-digit', day: '2-digit' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  useEffect(() => {
    if (!userId) {
      console.error('No user ID provided');
      return;
    }
    console.log(`Fetching profile for regular user with ID: ${userId}`);
    fetch(`${API_URL}/api/profile/regular/${userId}`)
      .then(response => {
        console.log('Response status:', response.status);
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        console.log('Fetched user details:', data);
        setUserDetails(data);
        setFormData(data);
      })
      .catch(error => {
        console.error('Error fetching user details:', error);
        setUserDetails({ error: 'Error fetching user details' });
      });
  }, [userId]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch(`${API_URL}/api/profile/regular/${userId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    })
      .then(response => response.json())
      .then(data => {
        alert(data.message);
        setEditing(false);
        setUserDetails(formData);
      })
      .catch(error => console.error('Error updating user details:', error));
  };

  const handleLogout = () => {
    localStorage.removeItem('userId');
    localStorage.removeItem('userType');
    navigate('/login');
    window.location.reload();
  };

  if (!userId) {
    return <p>Error: No user ID provided</p>;
  }

  if (userDetails.error) {
    return <p>{userDetails.error}</p>;
  }

  return (
    <div className="profile-container">
      <h1>Regular User Profile</h1>
      {editing ? (
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>First Name:</label>
            <input
              type="text"
              name="firstName"
              value={formData.firstName || ''}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>Last Name:</label>
            <input
              type="text"
              name="lastName"
              value={formData.lastName || ''}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>ID:</label>
            <input
              type="text"
              name="id"
              value={formData.id || ''}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>Number:</label>
            <input
              type="text"
              name="number"
              value={formData.number || ''}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>Address:</label>
            <input
              type="text"
              name="address"
              value={formData.address || ''}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>Date of Birth:</label>
            <input
              type="date"
              name="dob"
              value={formData.dob || ''}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email || ''}
              onChange={handleInputChange}
            />
          </div>
          <button type="submit">Save</button>
        </form>
      ) : (
        <div>
          <p><strong>First Name:</strong> {userDetails.firstName}</p>
          <p><strong>Last Name:</strong> {userDetails.lastName}</p>
          <p><strong>ID:</strong> {userDetails.id}</p>
          <p><strong>Number:</strong> {userDetails.number}</p>
          <p><strong>Address:</strong> {userDetails.address}</p>
          <p><strong>Date of Birth:</strong> {formatData(userDetails.dob)}</p>
          <p><strong>Email:</strong> {userDetails.email}</p>
          <button onClick={() => setEditing(true)}>Edit</button>
        </div>
      )}
      <button onClick={handleLogout} className="logout-button">Logout</button>
    </div>
  );
};

export default RegularProfile;
