  };

  const handleBooking = (timeSlot, skipMeal = false) => {
    // Booking logic...
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div className="dates">
      <NavbarBooking userId={userId} />
      <h2>Booked Dates</h2>
      <CalendarComponent
        events={events}
        onSelectSlot={handleSelectSlot}
      />
      <TimeSlotModal
        showModal={showModal}
        category={category}
        selectedDate={selectedDate}
        dayBooking={bookingStatus.some((booking) => booking.time_slot === "day")}
        nightBooking={bookingStatus.some(
          (booking) => booking.time_slot === "night"
        )}
        numOfPeople={numOfPeople}
        setNumOfPeople={setNumOfPeople}
        minGuests={minGuests}
        hallCapacity={hallCapacity}
        pricePerEvent={pricePerEvent}
        handleTimeSelection={handleTimeSelection}
        closeModal={() => setShowModal(false)}
      />
      <MealSelectionModal
        showMealsSelection={showMealsSelection}
        regularMeals={regularMeals}
        exclusiveMeal={exclusiveMeal}
        selectedMeal={selectedMeal}
        handleMealSelection={handleMealSelection}
        totalPrice={totalPrice}
        advancePayment={advancePayment}
        handleBooking={handleBooking}
        closeForm={closeForm}
      />
    </div>
  );
};

export default Dates;
