import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import ProfilePhotoSection from './ProfilePhotoSection';
import PhotoGallerySection from './PhotoGallerySection';
import BusinessProfileDetailsForm from './BusinessProfileDetailsForm';
import BusinessProfileDetailsDisplay from './BusinessProfileDetailsDisplay';
import { handleInputChange } from './InputChangeHandler';
import { handleProfilePhotoChange, handleProfilePhotoUpload } from './ProfilePhotoUploadHandler';
import { handleSubmit } from './FormSubmitHandler';
import { handleLogout } from './LogoutHandler';
import { fetchProfile } from './FetchProfileHandler';
import '../styles/styles.css';
imp
const BusinessProfile = () => {
  const [userDetails, setUserDetails] = useState({});
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});
  const [photos, setPhotos] = useState([]);
  const [newPhotos, setNewPhotos] = useState([]);
  const [profilePhoto, setProfilePhoto] = useState(null);
  const [newProfilePhoto, setNewProfilePhoto] = useState(null);
  const navigate = useNavigate();

  const userId = localStorage.getItem('userId');
  const userType = 'business';

  useEffect(() => {
    if (!userId) {
      console.error('No user ID provided');
      return;
    }
    fetchProfile(userId, setUserDetails, setFormData, setProfilePhoto, userType)
      .then(data => setPhotos(data.photos || []))
      .catch(error => console.error('Error fetching user details:', error));
  }, [userId]);

  const handlePhotoUpload = (e) => {
    e.preventDefault();
    const formData = new FormData();
    for (let i = 0; i < newPhotos.length; i++) {
      formData.append('photos', newPhotos[i]);
    }
    fetch(`${API_URL}/api/profile/business/${userId}/photos`, {
      method: 'POST',
      body: formData
    })
      .then(response => response.json())
      .then(data => {
        alert(data.message);
        setNewPhotos([]);
        fetch(`${API_URL}/api/profile/business/${userId}`)
          .then(response => response.json())
          .then(data => setPhotos(data.photos || []));
      })
      .catch(error => console.error('Error uploading photos:', error));
  };

  if (!userId) {
    return <p>Error: No user ID provided</p>;
  }

  if (userDetails.error) {
    return <p>{userDetails.error}</p>;
  }

  return (
    <div className="profile-container">
      <h1>Business Profile</h1>
      <ProfilePhotoSection
        profilePhoto={profilePhoto}
        handleProfilePhotoChange={(e) => handleProfilePhotoChange(e, setNewProfilePhoto)}
        handleProfilePhotoUpload={(e) => handleProfilePhotoUpload(e, userId, newProfilePhoto, setProfilePhoto, setNewProfilePhoto, userType)}
      />
      {editing ? (
        <BusinessProfileDetailsForm
          formData={formData}
          handleInputChange={(e) => handleInputChange(e, setFormData)}
          handleSubmit={(e) => handleSubmit(e, userId, formData, setEditing, setUserDetails, userType)}
        />
      ) : (
        <BusinessProfileDetailsDisplay
          userDetails={userDetails}
          setEditing={setEditing}
        />
      )}
      <PhotoGallerySection
        photos={photos}
        handlePhotoChange={(e) => setNewPhotos(e.target.files)}
        handlePhotoUpload={handlePhotoUpload}
      />
      <button onClick={() => handleLogout(navigate)} className="logout-button">Logout</button>
    </div>
  );
};

export default BusinessProfile;
