import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { API_URL } from '../constans';
import '../styles/styles.css';

const BusinessProfile = () => {
  const [userDetails, setUserDetails] = useState({});
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});
  const [photos, setPhotos] = useState([]);
  const [newPhotos, setNewPhotos] = useState([]);
  const [profilePhoto, setProfilePhoto] = useState(null);
  const [newProfilePhoto, setNewProfilePhoto] = useState(null);
  const navigate = useNavigate();

  const userId = localStorage.getItem('userId');

  useEffect(() => {
    if (!userId) {
      console.error('No user ID provided');
      return;
    }
    console.log(`Fetching profile for business user with ID: ${userId}`);
    fetch(`${API_URL}/api/profile/business/${userId}`)
      .then(response => {
        console.log('Response status:', response.status);
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        console.log('Fetched user details:', data);
        setUserDetails(data);
        setFormData(data);
        setPhotos(data.photos || []);
        setProfilePhoto(data.profilePhoto);
      })
      .catch(error => {
        console.error('Error fetching user details:', error);
        setUserDetails({ error: 'Error fetching user details' });
      });
  }, [userId]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handlePhotoChange = (e) => {
    setNewPhotos(e.target.files);
  };

  const handleProfilePhotoChange = (e) => {
    setNewProfilePhoto(e.target.files[0]);
  };

  const handlePhotoUpload = (e) => {
    e.preventDefault();
    const formData = new FormData();
    for (let i = 0; i < newPhotos.length; i++) {
      formData.append('photos', newPhotos[i]);
    }
    fetch(`${API_URL}/api/profile/business/${userId}/photos`, {
      method: 'POST',
      body: formData
    })
      .then(response => response.json())
      .then(data => {
        alert(data.message);
        setNewPhotos([]);
        fetch(`${API_URL}/api/profile/business/${userId}`)
          .then(response => response.json())
          .then(data => {
            setPhotos(data.photos || []);
          });
      })
      .catch(error => console.error('Error uploading photos:', error));
  };

  const handleProfilePhotoUpload = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('profilePhoto', newProfilePhoto);

    fetch(`${API_URL}/api/profile/business/${userId}/profile-photo`, {
      method: 'POST',
      body: formData
    })
      .then(response => response.json())
      .then(data => {
        alert(data.message);
        setNewProfilePhoto(null);
        setProfilePhoto(data.profilePhoto);
      })
      .catch(error => console.error('Error uploading profile photo:', error));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch(`${API_URL}/api/profile/business/${userId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    })
      .then(response => response.json())
      .then(data => {
        alert(data.message);
        setEditing(false);
        setUserDetails(formData);
      })
      .catch(error => console.error('Error updating user details:', error));
  };

  const handleLogout = () => {
    localStorage.removeItem('userId');
    localStorage.removeItem('userType');
    navigate('/login');
    window.location.reload();
  };

  if (!userId) {
    return <p>Error: No user ID provided</p>;
  }

  if (userDetails.error) {
    return <p>{userDetails.error}</p>;
  }

  return (
    <div className="profile-container">
      <h1>Business Profile</h1>
      {profilePhoto && <img src={profilePhoto} alt="Profile" className="profile-photo" />}
      <p><strong>Business Name:</strong> {userDetails.businessName}</p>
      <p><strong>Address:</strong> {userDetails.address}</p>
      <p><strong>Email:</strong> {userDetails.email}</p>
      <p><strong>Category:</strong> {userDetails.category}</p> {/* Display the category */}
      {editing ? (
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Business Name:</label>
            <input
              type="text"
              name="businessName"
              value={formData.businessName || ''}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>Address:</label>
            <input
              type="text"
              name="address"
              value={formData.address || ''}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email || ''}
              onChange={handleInputChange}
            />
          </div>
          <button type="submit">Save</button>
        </form>
      ) : (
        <div>
          <button onClick={() => setEditing(true)}>Edit</button>
        </div>
      )}
      <div>
        <h2>Photos</h2>
        <form onSubmit={handlePhotoUpload}>
          <input type="file" multiple onChange={handlePhotoChange} />
          <button type="submit">Upload Photos</button>
        </form>
        <div className="photo-gallery">
          {photos.map((photo, index) => (
            <div key={index}>
              <img src={photo} alt={`Photo ${index + 1}`} />
              <p>Photo {index + 1}</p>
            </div>
          ))}
        </div>
      </div>
      <button onClick={handleLogout} className="logout-button">Logout</button>
    </div>
  );
};

export default BusinessProfile;
