import React, { useState } from 'react';

const BusinessProfileDetailsDisplay = ({ userDetails, setEditing }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [hallCapacity, setHallCapacity] = useState(userDetails.hallCapacity || '');

  const handleEdit = () => {
    setIsEditing(true);
    setHallCapacity(userDetails.hallCapacity); // Set the current hall capacity when entering edit mode
  };

  const handleUpdate = async () => {
    // Logic to handle the update of hallCapacity
    // This could involve calling an API to update the user details
    // For example:
    // await updateUserDetails({ ...userDetails, hallCapacity });
    alert(`Updated hall capacity to: ${hallCapacity}`);
    setIsEditing(false); // Exit editing mode
  };

  return (
    <div>
      <p><strong>Business Name:</strong> {userDetails.businessName}</p>
      <p><strong>Address:</strong> {userDetails.address}</p>
      <p><strong>Email:</strong> {userDetails.email}</p>
      <p><strong>Category:</strong> {userDetails.category_name || 'N/A'}</p> {/* Display category_name */}
      {isEditing ? (
        <div>
          <label>Hall Capacity:</label>
          <input
            type="number"
            value={hallCapacity}
            onChange={(e) => setHallCapacity(e.target.value)}
            min="0" // Prevent negative input
          />
          <button onClick={handleUpdate}>Save</button>
          <button onClick={() => setIsEditing(false)}>Cancel</button>
        </div>
      ) : (
        <div>
          <p><strong>Hall Capacity:</strong> {userDetails.hallCapacity || 'N/A'}</p> {/* Display hallCapacity */}
          <button onClick={handleEdit}>Edit</button>
        </div>
      )}
    </div>
  );
};

export default BusinessProfileDetailsDisplay;
