import React, { useState } from "react";
import "../styles/styles.css";

const Services = () => {
  const [exclusiveMeal, setExclusiveMeal] = useState(null); // Single exclusive meal
  const [regularMeals, setRegularMeals] = useState([]);
  const [newMeal, setNewMeal] = useState({
    name: "",
    description: "",
    image: null,
  });
  const [showExclusiveForm, setShowExclusiveForm] = useState(false);
  const [showRegularForm, setShowRegularForm] = useState(false);

  const handleImageUpload = (e, mealType) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      if (mealType === "exclusive") {
        setExclusiveMeal({ ...newMeal, image: reader.result });
      } else {
        setNewMeal({ ...newMeal, image: reader.result });
      }
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const addExclusiveMeal = () => {
    if (newMeal.name && newMeal.description && newMeal.image) {
      setExclusiveMeal(newMeal);
      setNewMeal({ name: "", description: "", image: null }); // Reset form
      setShowExclusiveForm(false); // Hide form after adding
    } else {
      alert("Please fill in all fields for the exclusive meal.");
    }
  };

  const addRegularMeal = () => {
    if (newMeal.name && newMeal.description && newMeal.image) {
      setRegularMeals([...regularMeals, newMeal]);
      setNewMeal({ name: "", description: "", image: null }); // Reset form
      setShowRegularForm(false); // Hide form after adding
    } else {
      alert("Please fill in all fields for the regular meal.");
    }
  };

  const updateMeal = (mealType, index) => {
    const updatedMeal = prompt(
      "Enter new name, description and image URL separated by commas",
      `${newMeal.name},${newMeal.description},${newMeal.image}`
    );
    if (updatedMeal) {
      const [name, description, image] = updatedMeal.split(",");
      if (mealType === "exclusive") {
        setExclusiveMeal({ name, description, image });
      } else {
        const updatedMeals = [...regularMeals];
        updatedMeals[index] = { name, description, image };
        setRegularMeals(updatedMeals);
      }
    }
  };

  const removeMeal = (mealType, index) => {
    if (mealType === "exclusive") {
      setExclusiveMeal(null);
    } else {
      const updatedMeals = regularMeals.filter((_, i) => i !== index);
      setRegularMeals(updatedMeals);
    }
  };

  return (
    <div className='profile-container'>
      <h1>Hall Services</h1>

      <h2>Exclusive Meal</h2>
      {!showExclusiveForm && (
        <button onClick={() => setShowExclusiveForm(true)}>
          Add Exclusive Meal
        </button>
      )}
      {showExclusiveForm && (
        <div>
          <input
            type='text'
            placeholder='Meal Name'
            value={newMeal.name}
            onChange={(e) => setNewMeal({ ...newMeal, name: e.target.value })}
          />
          <input
            type='text'
            placeholder='Description'
            value={newMeal.description}
            onChange={(e) =>
              setNewMeal({ ...newMeal, description: e.target.value })
            }

          />
          <input
            type='file'
            onChange={(e) => handleImageUpload(e, "exclusive")}
          />
          <button onClick={addExclusiveMeal}>Save Exclusive Meal</button>
          <button onClick={() => setShowExclusiveForm(false)}>
            Cancel
          </button>{" "}
          {/* Cancel button */}
        </div>
      )}

      {exclusiveMeal && (
        <div className='meal-container'>
          <img
            src={exclusiveMeal.image}
            alt={exclusiveMeal.name}
            style={{ width: "100px", height: "100px" }}
          />
          <h3>{exclusiveMeal.name}</h3>
          <p>{exclusiveMeal.description}</p>
          <div className='dropdown'>
            <button className='dropbtn'>⋮</button>
            <div className='dropdown-content'>
              <button onClick={() => updateMeal("exclusive")}>Update</button>
              <button onClick={() => removeMeal("exclusive")}>Remove</button>
            </div>
          </div>
        </div>
      )}

      <h2>Regular Meals</h2>
      {!showRegularForm && (
        <button onClick={() => setShowRegularForm(true)}>
          Add Regular Meal
        </button>
      )}
      {showRegularForm && (
        <div>
          <input
            type='text'
            placeholder='Meal Name'
            value={newMeal.name}
            onChange={(e) => setNewMeal({ ...newMeal, name: e.target.value })}
          />
          <input
            type='text'
            placeholder='Description'
            value={newMeal.description}
            onChange={(e) =>
              setNewMeal({ ...newMeal, description: e.target.value })
            }
          />
          <input
            type='number'
            placeholder='Price'
            value={newMeal.price}
            onChange={(e) => setNewMeal({ ...newMeal, price: e.target.value })}
          />
          <input
            type='file'
            onChange={(e) => handleImageUpload(e, "regular")}
          />
          <button onClick={addRegularMeal}>Save Regular Meal</button>
          <button onClick={() => setShowRegularForm(false)}>Cancel</button>{" "}
          {/* Cancel button */}
        </div>
      )}

      <ul>
        {regularMeals.map((meal, index) => (
          <li key={index} className='meal-container'>
            <img
              src={meal.image}
              alt={meal.name}
              style={{ width: "100px", height: "100px" }}
            />
            <h3>{meal.name}</h3>
            <p>{meal.description}</p>
            <div className='dropdown'>
              <button className='dropbtn'>⋮</button>
              <div className='dropdown-content'>
                <button onClick={() => updateMeal("regular", index)}>
                  Update
                </button>
                <button onClick={() => removeMeal("regular", index)}>
                  Remove
                </button>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Services;
