import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import CalendarComponent from './Dates/CalendarComponent';
import ModalComponent from './Dates/ModalComponent';
import MealSelection from './Dates/MealSelection';
import TimeSlotSelection from './Dates/components/TimeSlotSelection';
import { handleInputChange } from './Dates/handlers/handleInputChange';
import { handleMealSelection } from './Dates/handlers/handleMealSelection';
import { handleTimeSelection } from './Dates/handlers/handleTimeSelection';
import { handleBookingSubmission } from './Dates/handlers/handleBookingSubmission';
import useFetchProfileEffect from './Dates/hooks/useFetchProfileEffect';
import '../styles/Dates.css';
import NavbarBooking from './NavbarBooking';

const Dates = () => {
  const { userId } = useParams();
  const [category, setCategory] = useState("");
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTimeSlot, setSelectedTimeSlot] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showMealsSelection, setShowMealsSelection] = useState(false);
  const [bookingStatus, setBookingStatus] = useState([]);
  const [userType, setUserType] = useState(null);
  const [numOfPeople, setNumOfPeople] = useState("");
  const [hallCapacity, setHallCapacity] = useState(null);
  const [minGuests, setMinGuests] = useState(null);
  const [exclusiveMeal, setExclusiveMeal] = useState(null);
  const [regularMeals, setRegularMeals] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [pricePerEvent, setPricePerEvent] = useState(0);
  const [advancePayment, setAdvancePayment] = useState(0);
  const [selectedMeal, setSelectedMeal] = useState(null);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    idNum: "",
    phoneNumber: "",
    email: "",
    num_of_people: 0,
    totalPrice: 0,
    priceRemaining: 0,
    paid: 0,
  });

  useFetchProfileEffect(userId, setCategory, setPricePerEvent, setHallCapacity, setMinGuests, setTotalPrice, setLoading, setEvents, setRegularMeals, setExclusiveMeal);

  const closeForm = () => {
    setSelectedMeal(null);
    setTotalPrice(pricePerEvent);
    setSelectedDate(null);
    setSelectedTimeSlot(null);
    setNumOfPeople(0);
    setFormData({
      firstName: "",
      lastName: "",
      idNum: "",
      phoneNumber: "",
      email: "",
      num_of_people: 0,
      totalPrice: 0,
      priceRemaining: 0,
      paid: 0,
    });
    setShowModal(false);
    setShowMealsSelection(false);
  };

  return (
    <div className='dates'>
      <NavbarBooking userId={userId} />
      <h2>Booked Dates</h2>
      <CalendarComponent
        events={events}
        localizer={localizer}
        onSelectSlot={({ start }) => handleSelectSlot(start, setSelectedDate, setBookingStatus, setShowModal, userType)}
      />
      <TimeSlotSelection
        category={category}
        dayBooking={bookingStatus.some((booking) => booking.time_slot === "day")}
        nightBooking={bookingStatus.some((booking) => booking.time_slot === "night")}
        handleTimeSelection={(time) => handleTimeSelection(time, category, numOfPeople, minGuests, hallCapacity, setShowModal, setShowMealsSelection, setSelectedTimeSlot)}
        pricePerEvent={pricePerEvent}
        closeModal={() => setShowModal(false)}
        showModal={showModal}
        setNumOfPeople={setNumOfPeople}
        numOfPeople={numOfPeople}
      />
      <MealSelection
        showMealsSelection={showMealsSelection}
        setShowMealsSelection={setShowMealsSelection}
        handleMealSelection={(meal) => handleMealSelection(meal, selectedMeal, setSelectedMeal, calculateTotalPrice, setTotalPrice, setAdvancePayment, pricePerEvent)}
        exclusiveMeal={exclusiveMeal}
        regularMeals={regularMeals}
        selectedMeal={selectedMeal}
        totalPrice={totalPrice}
        advancePayment={advancePayment}
        handleBooking={() => handleBookingSubmission(selectedTimeSlot, category, numOfPeople, selectedMeal, userId, selectedDate, totalPrice, advancePayment, closeForm, pricePerEvent, selectedMeal)}
        closeForm={closeForm}
      />
    </div>
  );
};

export default Dates;
