export const handleInputChange = (e, setFormData) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  export const handleProfilePhotoChange = (e, setNewProfilePhoto) => {
    setNewProfilePhoto(e.target.files[0]);
  };

  export const handleProfilePhotoUpload = async (e, userId, newProfilePhoto, setProfilePhoto, setNewProfilePhoto, API_URL) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('profilePhoto', newProfilePhoto);

    try {
      const response = await fetch(`${API_URL}/api/profile/regular/${userId}/profile-photo`, {
        method: 'POST',
        body: formData
      });
      const data = await response.json();
      alert(data.message);
      setNewProfilePhoto(null);
      setProfilePhoto(data.profilePhoto);
    } catch (error) {
      console.error('Error uploading profile photo:', error);
    }
  };

  export const handleSubmit = async (e, userId, formData, setEditing, setUserDetails, API_URL) => {
    e.preventDefault();
    try {
      const response = await fetch(`${API_URL}/api/profile/regular/${userId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const data = await response.json();
      alert(data.message);
      setEditing(false);
      setUserDetails(formData);
    } catch (error) {
      console.error('Error updating user details:', error);
    }
  };

  export const handleLogout = (navigate) => {
    localStorage.removeItem('userId');
    localStorage.removeItem('userType');
    navigate('/login');
    window.location.reload();
  };
