export const handleTimeSelection = ({
    time,
    category,
    numOfPeople,
    minGuests,
    hallCapacity,
    setShowModal,
    setShowMealsSelection,
    setTotalPrice,
    pricePerEvent,
    handleBooking,
  }) => {
    if (category === "Hall") {
      if (!numOfPeople || numOfPeople < minGuests || numOfPeople > hallCapacity) {
        alert(
          `The number of people must be between ${minGuests} and ${hallCapacity}.`
        );
        return;
      }
      setShowModal(false);
      setShowMealsSelection(true);
    } else {
      setTotalPrice(pricePerEvent);
      setShowModal(false);
      handleBooking(time);
    }
  };
