import React, { useState } from 'react';
import '../styles/styles.css';

const Services = () => {
  const [exclusiveMeal, setExclusiveMeal] = useState(null); // Single exclusive meal
  const [regularMeals, setRegularMeals] = useState([]);
  const [newMeal, setNewMeal] = useState({ name: '', description: '', image: null, price: '' });
  const [showExclusiveForm, setShowExclusiveForm] = useState(false);
  const [showRegularForm, setShowRegularForm] = useState(false);

  const handleImageUpload = (e, mealType) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      if (mealType === 'exclusive') {
        setExclusiveMeal({ ...newMeal, image: reader.result });
      } else {
        setNewMeal({ ...newMeal, image: reader.result });
      }
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const addExclusiveMeal = () => {
    if (newMeal.name && newMeal.description && newMeal.image && newMeal.price) {
      setExclusiveMeal(newMeal);
      setNewMeal({ name: '', description: '', image: null, price: '' }); // Reset form
      setShowExclusiveForm(false); // Hide form after adding
    } else {
      alert("Please fill in all fields for the exclusive meal.");
    }
  };

  const addRegularMeal = () => {
    if (newMeal.name && newMeal.description && newMeal.image && newMeal.price) {
      setRegularMeals([...regularMeals, newMeal]);
      setNewMeal({ name: '', description: '', image: null, price: '' }); // Reset form
      setShowRegularForm(false); // Hide form after adding
    } else {
      alert("Please fill in all fields for the regular meal.");
    }
  };

  return (
    <div className="profile-container">
      <h1>Hall Services</h1>

      <h2>Exclusive Meal</h2>
      {!showExclusiveForm && (
        <button onClick={() => setShowExclusiveForm(true)}>Add Exclusive Meal</button>
      )}
      {showExclusiveForm && (
        <div>
          <input
            type="text"
            placeholder="Meal Name"
            value={newMeal.name || ''}
            onChange={(e) => setNewMeal({ ...newMeal, name: e.target.value })}
          />
          <input
            type="text"
            placeholder="Description"
            value={newMeal.description || ''}
            onChange={(e) => setNewMeal({ ...newMeal, description: e.target.value })}
          />
          <input
            type="number"
            placeholder="Price"
            value={newMeal.price || ''}
            onChange={(e) => setNewMeal({ ...newMeal, price: e.target.value })}
          />
          <input
            type="file"
            onChange={(e) => handleImageUpload(e, 'exclusive')}
          />
          <button onClick={addExclusiveMeal}>Save Exclusive Meal</button>
          <button onClick={() => setShowExclusiveForm(false)}>Cancel</button> {/* Cancel button */}
        </div>
      )}

      {exclusiveMeal && (
        <div className="meal-container">
          <img src={exclusiveMeal.image} alt={exclusiveMeal.name} style={{ width: '100px', height: '100px' }} />
          <h3>{exclusiveMeal.name}</h3>
          <p>{exclusiveMeal.description}</p>
          <p>{exclusiveMeal.price}</p>
          <div className="dropdown">
            <button className="dropbtn">⋮</button>
            <div className="dropdown-content">
              <button>Update</button>
              <button>Remove</button>
            </div>
          </div>
        </div>
      )}

      <h2>Regular Meals</h2>
      {!showRegularForm && (
        <button onClick={() => setShowRegularForm(true)}>Add Regular Meal</button>
      )}
      {showRegularForm && (
        <div>
          <input
            type="text"
            placeholder="Meal Name"
            value={newMeal.name || ''}
            onChange={(e) => setNewMeal({ ...newMeal, name: e.target.value })}
          />
          <input
            type="text"
            placeholder="Description"
            value={newMeal.description || ''}
            onChange={(e) => setNewMeal({ ...newMeal, description: e.target.value })}
          />
          <input
            type="number"
            placeholder="Price"
            value={newMeal.price || ''}
            onChange={(e) => setNewMeal({ ...newMeal, price: e.target.value })}
          />
          <input
            type="file"
            onChange={(e) => handleImageUpload(e, 'regular')}
          />
          <button onClick={addRegularMeal}>Save Regular Meal</button>
          <button onClick={() => setShowRegularForm(false)}>Cancel</button> {/* Cancel button */}
        </div>
      )}

      <ul>
        {regularMeals.map((meal, index) => (
          <li key={index} className="meal-container">
            <img src={meal.image} alt={meal.name} style={{ width: '100px', height: '100px' }} />
            <h3>{meal.name}</h3>
            <p>{meal.description}</p>
            <p>{meal.price}</p>
            <div className="dropdown">
              <button className="dropbtn">⋮</button>
              <div className="dropdown-content">
                <button>Update</button>
                <button>Remove</button>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Services;
