// src/pages/Register.js
import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import RegularUserRegister from '../components/RegularUserRegister';
import BusinessUserRegister from '../components/BusinessUserRegister';
import '../styles/styles.css';

const Register = () => {
  const [formType, setFormType] = useState(null);
  const location = useLocation();

  useEffect(() => {
    setFormType(null); // Reset form type when the component mounts or the path changes
  }, [location]);

  const handleButtonClick = (type) => {
    setFormType(type);
  };

  const handleBackClick = () => {
    setFormType(null);
  };

  return (
    <div className="register-container">
      <h1>Register</h1>
      {!formType && (
        <div className="button-container">
          <button onClick={() => handleButtonClick('regular')}>Regular User Registration</button>
          <button onClick={() => handleButtonClick('business')}>Business User Registration</button>
        </div>
      )}
      {formType === 'regular' && (
        <>
          <button className="back-button" onClick={handleBackClick}>←</button>
          <RegularUserRegister />
        </>
      )}
      {formType === 'business' && (
        <>
          <button className="back-button" onClick={handleBackClick}>←</button>
          <BusinessUserRegister />
        </>
      )}
    </div>
  );
};

export default Register;
