// src/components/Dates.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import '../styles/BookingCalendar.css';
import NavbarBooking from '../components/NavbarBooking'; // Import NavbarBooking
import '../styles/Dates.css';

const localizer = momentLocalizer(moment);

const Dates = () => {
  const { userId } = useParams();
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedDate, setSelectedDate] = useState(null);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    idNum: '',
    phoneNumber: ''
  });
  const [showModal, setShowModal] = useState(false);

  const userType = localStorage.getItem('userType'); // Get the user type from localStorage

  useEffect(() => {
    const fetchBookedDates = async () => {
      try {
        const response = await fetch(`/api/profile/business/bookings/${userId}/booked-dates`);
        if (!response.ok) {
          throw new Error('Failed to fetch booked dates');
        }
        const data = await response.json();
        const formattedEvents = data.bookedDates.map((event) => ({
          start: new Date(event.booking_date),
          end: new Date(event.booking_date),
          title: `Booked: ${event.time_slot}`,
        }));
        setEvents(formattedEvents);
      } catch (error) {
        console.error('Error fetching booked dates:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchBookedDates();
  }, [userId]);

  const handleSelectSlot = ({ start }) => {
    const now = new Date();
    if (userType !== 'regular' || start <= now) {
      alert('Please select a future date.');
      return;
    }
    setSelectedDate(start);
    setShowModal(true);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleBooking = () => {
    if (!formData.firstName || !formData.lastName || !formData.idNum || !formData.phoneNumber) {
      alert('Please fill in all the form fields.');
      return;
    }

    fetch(`/api/bookings/regular`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        business_id: userId,
        date: selectedDate.toISOString().split('T')[0],
        time_slot: 'day', // Assuming a single time slot for regular users
        ...formData,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.message === 'Booking created successfully') {
          setEvents((prevEvents) => [
            ...prevEvents,
            {
              ...formData,
              start: new Date(selectedDate),
              end: new Date(selectedDate),
              title: `${formData.firstName} ${formData.lastName}`,
            },
          ]);
          setShowModal(false);
          setFormData({ firstName: '', lastName: '', idNum: '', phoneNumber: '' });
          setSelectedDate(null);
        } else {
          alert(data.message);
        }
      })
      .catch((error) => console.error('Error storing date selection:', error));
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div className="dates">
      <NavbarBooking userId={userId} /> {/* Add NavbarBooking */}
      <h2>Booked Dates</h2>
      <Calendar
        localizer={localizer}
        events={events}
        selectable={userType === 'regular'} // Allow selecting only for regular users
        onSelectSlot={handleSelectSlot}
        defaultView="month"
        views={['month', 'agenda']}
        style={{ height: 500 }}
      />
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h3>Select Time Slot for {moment(selectedDate).format('MMMM Do YYYY')}</h3>
            <button onClick={() => setShowModal(false)}>Cancel</button>
          </div>
          <div className="form-content">
            <h3>Booking Details</h3>
            <form>
              <div>
                <label>First Name:</label>
                <input
                  type="text"
                  name="firstName"
                  value={formData.firstName}
                  onChange={handleInputChange}
                />
              </div>
              <div>
                <label>Last Name:</label>
                <input
                  type="text"
                  name="lastName"
                  value={formData.lastName}
                  onChange={handleInputChange}
                />
              </div>
              <div>
                <label>ID Number:</label>
                <input
                  type="text"
                  name="idNum"
                  value={formData.idNum}
                  onChange={handleInputChange}
                />
              </div>
              <div>
                <label>Phone Number:</label>
                <input
                  type="text"
                  name="phoneNumber"
                  value={formData.phoneNumber}
                  onChange={handleInputChange}
                />
              </div>
              <button type="button" onClick={handleBooking}>
                Book
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dates;
