import React from "react";

const MealSelection = ({
  exclusiveMeal,
  regularMeals,
  selectedMeal,
  onSelectMeal,
}) => {
  return (
    <div className="meal-selection">
      <h3>Select a Meal</h3>
      <h4>Exclusive Meals</h4>
      <ul>
        {exclusiveMeal && (
          <li onClick={() => onSelectMeal(exclusiveMeal)}>
            <input
              type="radio"
              name="meal"
              value={exclusiveMeal.id}
              checked={selectedMeal?.id === exclusiveMeal.id}
              onChange={() => onSelectMeal(exclusiveMeal)}
            />
            {exclusiveMeal.name} - ${exclusiveMeal.price}
            <img
              src={exclusiveMeal.image}
              alt={exclusiveMeal.name}
              className="meal-photo"
            />
          </li>
        )}
      </ul>
      <h4>Regular Meals</h4>
      <ul>
        {regularMeals.map((meal) => (
          <li key={meal.id} onClick={() => onSelectMeal(meal)}>
            <input
              type="radio"
              name="meal"
              value={meal.id}
              checked={selectedMeal?.id === meal.id}
              onChange={() => onSelectMeal(meal)}
            />
            {meal.name} - ${meal.price}
            <img src={meal.image} alt={meal.name} className="meal-photo" />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MealSelection;
