import React, { useState } from 'react';

const BusinessProfileDetailsDisplay = ({ userDetails, setEditing }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    businessName: userDetails.businessName,
    address: userDetails.address,
    email: userDetails.email,
    hallCapacity: userDetails.hallCapacity || '',
    category_id: userDetails.category_id,
  });

  const handleEdit = () => {
    setIsEditing(true);
    setFormData({
      businessName: userDetails.businessName,
      address: userDetails.address,
      email: userDetails.email,
      hallCapacity: userDetails.hallCapacity || '',
      category_id: userDetails.category_id,
    }); // Set the current values when entering edit mode
  };

  const handleUpdate = async () => {
    // Logic to handle the update of all fields
    try {
      const response = await fetch(`http://localhost:3000/api/bu/${userDetails.id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData), // Send all form data
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        setIsEditing(false); // Exit editing mode
      } else {
        alert(data.message || "Error updating business details");
      }
    } catch (error) {
      console.error("Error updating business details:", error);
      alert("Error updating business details. Please try again.");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  return (
    <div>
      <p><strong>Business Name:</strong> {userDetails.businessName}</p>
      <p><strong>Address:</strong> {userDetails.address}</p>
      <p><strong>Email:</strong> {userDetails.email}</p>
      <p><strong>Category:</strong> {userDetails.category_name || 'N/A'}</p>

      {userDetails.category_name === 'Hall' && (
        <>
          {isEditing ? (
            <div>

              <input
                type="number"
                name="hallCapacity"
                value={formData.hallCapacity}
                onChange={handleChange}
                min="0" // Prevent negative input
              />
              <button onClick={handleUpdate}>Save</button>
              <button onClick={() => setIsEditing(false)}>Cancel</button>
            </div>
          ) : (
            <div>
              <p><strong>Hall Capacity:</strong> {userDetails.hallCapacity || 'N/A'}</p>
            </div>
          )}
        </>
      )}

      {/* Always show the Edit button */}
      <button onClick={handleEdit}>Edit</button>
    </div>
  );
};

export default BusinessProfileDetailsDisplay;
