import React, { useReducer } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const initialState = {
  date: new Date(),
  firstName: '',
  lastName: '',
  idNum: '',
  phoneNumber: ''
};

const reducer = (state, action) => {
  switch (action.type) {
    case 'SET_DATE':
      return { ...state, date: action.payload };
    case 'SET_FIRST_NAME':
      return { ...state, firstName: action.payload };
    case 'SET_LAST_NAME':
      return { ...state, lastName: action.payload };
    case 'SET_ID_NUM':
      return { ...state, idNum: action.payload };
    case 'SET_PHONE_NUMBER':
      return { ...state, phoneNumber: action.payload };
    default:
      return state;
  }
};

const DatesComponent = () => {
  const [state, dispatch] = useReducer(reducer, initialState);

  return (
    <div className="profile-container">
      <h1>Reserve a Date</h1>
      <Calendar 
        onChange={(date) => dispatch({ type: 'SET_DATE', payload: date })} 
        value={state.date} 
      />
      <form>
        <div className="form-group">
          <label>First Name:</label>
          <input 
            type="text" 
            value={state.firstName} 
            onChange={(e) => dispatch({ type: 'SET_FIRST_NAME', payload: e.target.value })} 
            required 
          />
        </div>
        <div className="form-group">
          <label>Last Name:</label>
          <input 
            type="text" 
            value={state.lastName} 
            onChange={(e) => dispatch({ type: 'SET_LAST_NAME', payload: e.target.value })} 
            required 
          />
        </div>
        <div className="form-group">
          <label>ID Number:</label>
          <input 
            type="text" 
            value={state.idNum} 
            onChange={(e) => dispatch({ type: 'SET_ID_NUM', payload: e.target.value })} 
            required 
          />
        </div>
        <div className="form-group">
          <label>Phone Number:</label>
          <input 
            type="text" 
            value={state.phoneNumber} 
            onChange={(e) => dispatch({ type: 'SET_PHONE_NUMBER', payload: e.target.value })} 
            required 
          />
        </div>
        <button type="submit">Reserve</button>
      </form>
    </div>
  );
};

export default DatesComponent;
