// src/pages/CategoryUserProfile.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { API_URL } from '../constans';
import '../styles/CategoryUserProfile.css';

const CategoryUserProfile = () => {
  const { userId } = useParams();
  const [user, setUser] = useState(null);
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    fetch(`${API_URL}/api/users/${userId}`)
      .then(response => response.json())
      .then(data => {
        setUser(data);
        setPhotos(data.photos); // Assuming the API returns a user object with a photos array
      })
      .catch(error => console.error('Error fetching user data:', error));
  }, [userId]);

  if (!user) {
    return <p>Loading...</p>;
  }

  return (
    <div className="user-profile-container">
      <h1>{user.businessname}</h1>
      <p>{user.email}</p>
      <div className="photos-grid">
        {photos.map(photo => (
          <img key={photo.id} src={photo.url} alt={photo.description || 'User Photo'} />
        ))}
      </div>
    </div>
  );
};

export default CategoryUserProfile;
